script:
frappe.ui.form.on('Packing Slip', {
    validate: function(frm, cdt, cdn) {
        // make calculation on the fields
        var w = flt(frm.doc.package_width);
        var l = flt(frm.doc.package_length);
        var h = flt(frm.doc.package_height);
        var v = w * l * h;
        frm.set_value('volume', v);
        frm.refresh_field('volume');
    }
});
