content:
{% if doc.company %}
    {% set myCompName = doc.company %}
{% endif %}

<div class="row-responsive" style="display: flex;">
    {% if myCompName %}
        {% set C = frappe.get_doc("Company", myCompName) %}
        {% set CID = frappe.get_doc("Corporate Identity", C.corporate_identity) %}

        <div class="col-xs-1" style="padding-left: 5px; padding-right: 0;">
            {% if CID %}
                <img src="{{CID.logo_512}}" style="height: 50px; float: right;">
            {% endif %}
        </div>

        <div class="col-xs-8">
            <div style="font-weight: bold; font-size: 20px;">
                {{ myCompName }}</div>
            <div style="font-style: italic; font-size: 14px;">
                {% if CID %}
                    {{ CID.tagline or '' }}
                {% endif %}
            </div>
        </div>
    {% endif %}

    <div class="col-xs-3" style="border: solid 1px; padding-left: 20px; padding-top: 2px; font-size: 10px;">
        <div class="row"><b>{{ _("Status") }}:</b>
            {% set status = "" %}
            {% if doc.docstatus == 1 %}
                {% set status = "Active" %}
            {% elif doc.docstatus == 0 %}
                {% set status = "Draft" %}
            {% endif %}
            {{ _(status) }}
        </div>

        <div class="row"><b>{{ _("Amended") }}:</b> {% if doc.amended_from %} {{_("Yes")}} {%else%} {{_("No")}} {% endif %}</div>
        <div class="row"><b>{{ _("Date") }}:</b> {{ _(frappe.utils.get_datetime(doc.transaction_date).strftime("%d-%b-%Y",)) }}</div>
        <div class="row"><b>Page: </b>{{ _("{0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }} </div>
    </div>
</div>

<div class="row">
    <hr class="top-bottom-line">
</div>

footer:
{% set C = frappe.get_doc("Company", doc.company) %}
{% if C %}
    {% set CID = frappe.get_doc("Corporate Identity", C.corporate_identity) %}

    <!-- FOOTER (every page) -->
    <div class="visible-pdf" id="footer-html">
       <div class="row">
           <hr class="top-bottom-line">
       </div>
        <div class="row">
            <div class="col-xs-5">
                <b>{{ frappe.db.get_value("Company", doc.company, "company_name") or ''}}</b><br>
                {% set links = frappe.get_all('Dynamic Link', filters={'link_doctype': 'Company', 'link_name': doc.company, 'parenttype': 'Address'}, fields=['parent']) %}
                {% if links %}
                    {{frappe.db.get_value("Address", links[0].parent, "address_line1") or ''}}<br>
                    {{frappe.db.get_value("Address", links[0].parent, "address_line2") or ''}}<br>
                    {{frappe.db.get_value("Address", links[0].parent, "city") or ''}}
                    {{frappe.db.get_value("Address", links[0].parent, "pincode") or ''}}
                    {{frappe.db.get_value("Address", links[0].parent, "country") or ''}}
                {% endif %}
            </div>
            <div class="col-xs-4">
                {% if C.phone_no %} Telp: {{ C.phone_no or ''}}<br> {% endif %}
                {% if C.fax %} Fax: {{ C.fax or ''}}<br>  {% endif %}
                {% if C.email %} Email: {{ C.email or ''}}<br>  {% endif %}
                {% if C.website %} Web: {{ C.website or ''}}<br>  {% endif %}
            </div>
            <div class="col-xs-3">
                <img src="http://bwipjs-api.metafloor.com/?bcid=qrcode&amp;text={{ frappe.utils.get_url_to_form(doc.doctype, doc.name)|urlencode }}" style="height: 70px;">
            </div>
        </div>
    </div>
{% endif %}

<style>
    hr.top-bottom-line {
        border-top: 3px solid {% if CID %}{{ CID.color_1 }}{% else %} #193040 {% endif %};
    }
</style>
